import React, { useState } from "react";
import "./ProfileForm.css";

const initialNeedRoomFormState = {
  rank: "",
  gender: "M",
  bedType: "",
  preferedBlocks: ["A", "A", "A"],
  contactNumber: "",
  habits: "",
  description: "",
};
const initialNeedRoomMateFormState = {
  rank: "",
  gender: "M",
  noOfBeds: "",
  preferedBlocks: ["A", "A", "A"],
  contactNumber: "",
  habits: "",
  description: "",
};

let blocks = [
  "A",
  "B",
  "C",
  "D",
  "E",
  "F",
  "G",
  "H",
  "I",
  "J",
  "K",
  "L",
  "M",
  "N",
  "P",
  "Q",
  "R",
];

class ProfileForm extends React.Component {
  constructor() {
    super();
    this.state = {
      fields: {},
      errors: {},
    };

    this.handleChange = this.handleChange.bind(this);
    this.submituserRegistrationForm =
      this.submituserRegistrationForm.bind(this);
  }

  handleChange(e) {
    let fields = this.state.fields;
    fields[e.target.name] = e.target.value;
    this.setState({
      fields,
    });
  }

  submituserRegistrationForm(e) {
    e.preventDefault();
    if (this.validateForm()) {
      let fields = {};
      fields["firstname"] = "";
      fields["lastname"] = "";
      fields["emailid"] = "";
      fields["mobileno"] = "";
      this.setState({ fields: fields });
      alert("Form submitted");
    }
  }

  validateForm() {
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    if (!fields["firstname"]) {
      formIsValid = false;
      errors["firstname"] = "*Please enter your firstname.";
    }

    if (typeof fields["firstname"] !== "undefined") {
      if (!fields["firstname"].match(/^[a-zA-Z ]*$/)) {
        formIsValid = false;
        errors["firstname"] = "*Please enter alphabet characters only.";
      }
    }
    if (!fields["lastname"]) {
      formIsValid = false;
      errors["lastname"] = "*Please enter your lastname.";
    }

    if (typeof fields["lastname"] !== "undefined") {
      if (!fields["lastname"].match(/^[a-zA-Z ]*$/)) {
        formIsValid = false;
        errors["lastname"] = "*Please enter alphabet characters only.";
      }
    }

    if (!fields["emailid"]) {
      formIsValid = false;
      errors["emailid"] = "*Please enter your email-ID.";
    }

    if (typeof fields["emailid"] !== "undefined") {
      //regular expression for email validation
      var pattern = new RegExp(
        /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
      );
      if (!pattern.test(fields["emailid"])) {
        formIsValid = false;
        errors["emailid"] = "*Please enter valid email-ID.";
      }
    }

    if (!fields["mobileno"]) {
      formIsValid = false;
      errors["mobileno"] = "*Please enter your mobile no.";
    }

    if (typeof fields["mobileno"] !== "undefined") {
      if (!fields["mobileno"].match(/^[0-9]{10}$/)) {
        formIsValid = false;
        errors["mobileno"] = "*Please enter valid mobile no.";
      }
    }

    this.setState({
      errors: errors,
    });
    return formIsValid;

    function NeedRoom() {
      const [needRoom, setNeedRoom] = useState(true);
      const [needRoomForm, setNeedRoomForm] = useState(
        initialNeedRoomFormState
      );
      const [needRoomMateForm, setNeedRoomMateForm] = useState(
        initialNeedRoomMateFormState
      );

      function needRoomFormOnChangeHandler(e, index) {
        let { name, value } = e.target;

        //manage gender  state
        let gender = e.target.getAttribute("data-gender");
        if (gender) {
          let change = {};
          change["gender"] = gender;
          setNeedRoomForm((prev) => {
            return { ...prev, ...change };
          });
        }

        let change = {};
        change[name] = value;
        setNeedRoomForm((prev) => {
          return { ...prev, ...change };
        });
      }
    }
  }
  render() {
    return (
      <div id="main-registration-container">
        <div id="register">
          <form
            method="post"
            name="userRegistrationForm"
            onSubmit={this.submituserRegistrationForm}
          >
            <label>First Name*</label>
            <input
              type="text"
              name="firstname"
              value={this.state.fields.username}
              onChange={this.handleChange}
            />
            <div className="errorMsg">{this.state.errors.firstname}</div>
            <label>Last Name*</label>
            <input
              type="text"
              name="firstname"
              value={this.state.fields.username}
              onChange={this.handleChange}
            />
            <div
              style={{ gridAutoRows: "120px" }}
              className="grid grid-cols-1 items-center"
            >
              <div className="containerr  max-h-[80px]">
                <div className="label">Gender*</div>
                <div className="flex">
                  <div
                    data-gender="M"
                    name="gender"
                    className={`mr-6  bg-[#D9D9D9] rounded-[10px] py-2 px-8 text-center cursor-pointer ${
                      needRoomForm["gender"] === "M"
                        ? "border border-black"
                        : ""
                    }`}
                    onClick={needRoomFormOnChangeHandler}
                  >
                    M
                  </div>
                  <div
                    data-gender="F"
                    name="gender"
                    className={`bg-[#D9D9D9] rounded-[10px] py-2 px-8 text-center cursor-pointer ${
                      needRoomForm["gender"] === "F"
                        ? "border border-black"
                        : ""
                    }`}
                    onClick={needRoomFormOnChangeHandler}
                  >
                    F
                  </div>
                </div>
              </div>
              <div className="errorMsg">{this.state.errors.lastname}</div>
              <label>Email*</label>
              <input
                type="text"
                name="emailid"
                value={this.state.fields.emailid}
                onChange={this.handleChange}
              />
              <div className="errorMsg">{this.state.errors.emailid}</div>
              <label>Contact Number*</label>
              <input
                type="text"
                name="mobileno"
                value={this.state.fields.mobileno}
                onChange={this.handleChange}
              />
              <div className="errorMsg">{this.state.errors.mobileno}</div>

              <input type="submit" className="button" value="Register" />
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default ProfileForm;
