/* eslint-disable array-callback-return */
import React, { useContext, useState } from "react";
import list from "../../data.js";
import list2 from "../../data2.js";
import { ListingContext } from "../../Context/listing-context";
import Cards from "./Cards.jsx";
import RoomCards from "./RoomCards.jsx";

export const Listing = () => {
  const { cartItems } = useContext(ListingContext);
  const { cartItems2 } = useContext(ListingContext);
  const [currentListIndex, setCurrentListIndex] = useState(0);

  const lists = [list, list2];
  const cartItemsArray = [cartItems, cartItems2];

  const currentList = lists[currentListIndex];
  const currentCartItems = cartItemsArray[currentListIndex];

  const nextListIndex = (currentListIndex + 1) % lists.length;

  return (
    <>
      <h2>Listing</h2>
      <div className="tab-content">
        <div className="cards">
          {currentList.map((item) => {
            if (currentCartItems[item.id] !== 0) {
              return currentListIndex === 0 ? (
                <Cards item={item} key={item.id} />
              ) : (
                <RoomCards item={item} key={item.id} />
              );
            }
          })}
        </div>
      </div>
      <button onClick={() => setCurrentListIndex(nextListIndex)}>
        Switch List
      </button>
    </>
  );
};
