/* eslint-disable array-callback-return */
import React, { useContext } from "react";
import list from "../../data.js";
import list2 from "../../data2.js";
import { ListingContext } from "../../Context/listing-context";
import Cards from "./Cards.jsx";
import RoomCards from "./RoomCards.jsx";

export const Listing = () => {
  const { cartItems } = useContext(ListingContext);
  const { cartItems2 } = useContext(ListingContext);

  // Combine both lists
  const combinedList = [...list, ...list2];

  // Create a function to sort items based on whether they are in the cart
  const sortItems = (a, b) => {
    const aInCart = cartItems[a.id] !== 0 || cartItems2[a.id2] !== 0;
    const bInCart = cartItems[b.id] !== 0 || cartItems2[b.id2] !== 0;

    if (aInCart && !bInCart) return -1;
    if (!aInCart && bInCart) return 1;
    return 0;
  };

  return (
    <>
      <h2>Listing</h2>
      <div className="tab-content">
        <div className="cards">
          {Promise.all(
            combinedList.map(async (item) => {
              // You might want to add some loading state logic here if needed
              return item;
            })
          ).then((sortedList) => {
            sortedList.sort(sortItems);
            return sortedList.map((item) => {
              if (item.hasOwnProperty("id")) {
                if (cartItems[item.id] !== 0) {
                  return <Cards item={item} key={item.id} />;
                }
              } else if (item.hasOwnProperty("id2")) {
                if (cartItems2[item.id2] !== 0) {
                  return <RoomCards item={item} key={item.id2} />;
                }
              }
            });
          })}
        </div>
      </div>
    </>
  );
};
