import React, { useContext } from "react";
import list from "../../data.js";
import list2 from "../../data2.js";
import { ListingContext } from "../../Context/listing-context";
import Cards from "./Cards.jsx";
import RoomCards from "./RoomCards.jsx";

export const Listing = () => {
  const { cartItems, cartItems2 } = useContext(ListingContext);

  const combinedList = [...list, ...list2];

  const sortItems = async () => {
    const promises = combinedList.map(async (item) => {
      // Perform any async operations here if needed
      return {
        item,
        inCart: cartItems[item.id] !== 0 || cartItems2[item.id2] !== 0,
      };
    });

    const itemsWithCartStatus = await Promise.all(promises);

    itemsWithCartStatus.sort((a, b) => {
      if (a.inCart && !b.inCart) return -1;
      if (!a.inCart && b.inCart) return 1;
      return 0;
    });

    return itemsWithCartStatus.map((data) => {
      const { item, inCart } = data;
      if (inCart) {
        if (item.hasOwnProperty("id")) {
          return <Cards item={item} key={item.id} />;
        } else if (item.hasOwnProperty("id2")) {
          return <RoomCards item={item} key={item.id2} />;
        }
      }
      return null; // Return null for items that are not in the cart
    });
  };

  return (
    <>
      <h2>Listing</h2>
      <div className="tab-content">
        <div className="cards">
          {sortItems().then((sortedItems) => sortedItems)}
        </div>
      </div>
    </>
  );
};
