/* eslint-disable array-callback-return */
import React, { useContext } from "react";
import list from "../../data.js";
import list2 from "../../data2.js";
import { ListingContext } from "../../Context/listing-context";
import Cards from "./Cards.jsx";
import RoomCards from "./RoomCards.jsx";

export const Listing = () => {
  const { cartItems, cartItems2 } = useContext(ListingContext);

  // Combine the items from list and list2
  const combinedItems = [...list, ...list2];

  // Sort the combined items based on selected cards
  combinedItems.sort((a, b) => {
    const aIsSelected = cartItems[a.id] !== 0 || cartItems2[a.id2] !== 0;
    const bIsSelected = cartItems[b.id] !== 0 || cartItems2[b.id2] !== 0;
    return bIsSelected - aIsSelected;
  });

  return (
    <>
      <h2>Listing</h2>
      <div className="tab-content">
        <div className="cards">
          {combinedItems.map((item) => {
            if (cartItems[item.id] !== 0) {
              return <Cards item={item} key={item.id} />;
            }
            if (cartItems2[item.id2] !== 0) {
              return <RoomCards item={item} key={item.id2} />;
            }
            return null;
          })}
        </div>
      </div>
    </>
  );
};
